script:
frappe.ui.form.on('Leave Application', {
	refresh: function(frm) {
		render_calendar_link(frm);
		check_incapacity_field(frm);
	},
	status: function(frm) {
		render_calendar_link(frm);
	},
	leave_type: function(frm) {
		check_incapacity_field(frm);
	}
});

function check_incapacity_field(frm) {
	// Check if this leave type requires incapacity declaration (sick leave)
	if (!frm.doc.leave_type) {
		frm.set_df_property('incapacity_to_work', 'hidden', 1);
		frm.set_df_property('incapacity_to_work', 'reqd', 0);
		frm.set_df_property('caring_for_sick_loved_one', 'hidden', 1);
		frm.set_df_property('caring_for_sick_loved_one', 'reqd', 0);
		return;
	}

	frappe.call({
		method: 'flexitime.flexitime.doctype.presence_type.presence_type.get_requires_incapacity_declaration',
		args: { leave_type: frm.doc.leave_type },
		callback: function(r) {
			const requires = r.message || false;
			// Show/hide incapacity_to_work (Percent field) - mandatory when visible
			frm.set_df_property('incapacity_to_work', 'hidden', !requires);
			frm.set_df_property('incapacity_to_work', 'reqd', requires);
			// Show/hide caring_for_sick_loved_one (Checkbox) - optional when visible
			frm.set_df_property('caring_for_sick_loved_one', 'hidden', !requires);
			frm.set_df_property('caring_for_sick_loved_one', 'reqd', 0);
			if (!requires) {
				// Clear both fields when switching away from sick leave
				frm.set_value('incapacity_to_work', null);
				frm.set_value('caring_for_sick_loved_one', 0);
			}
		}
	});
}

function render_calendar_link(frm) {
	if (frm.doc.status !== 'Approved' || !frm.doc.from_date || !frm.doc.to_date) {
		frm.set_df_property('google_calendar_link', 'options', '');
		return;
	}

	// Check if we have an actual calendar event URL (event was created in Google Calendar)
	if (frm.doc.google_calendar_event_url) {
		// Show link to the actual event
		const html = `
			<div class="calendar-link-container" style="padding: 8px 0;">
				<p style="margin-bottom: 8px; color: var(--text-muted); font-size: var(--text-sm);">
					This leave has been added to the shared Leaves Calendar:
				</p>
				<a href="${frm.doc.google_calendar_event_url}" target="_blank" class="btn btn-primary btn-sm" style="display: inline-flex; align-items: center; gap: 6px;">
					<svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
						<path d="M19.5 3h-15A1.5 1.5 0 003 4.5v15A1.5 1.5 0 004.5 21h15a1.5 1.5 0 001.5-1.5v-15A1.5 1.5 0 0019.5 3zM12 18a6 6 0 110-12 6 6 0 010 12zm0-10.5a4.5 4.5 0 100 9 4.5 4.5 0 000-9z"/>
						<path d="M12 8v4l3 1.5"/>
					</svg>
					View in Google Calendar
				</a>
				<p style="margin-top: 8px; color: var(--text-muted); font-size: var(--text-xs);">
					The employee will receive a calendar invitation. All staff can view and edit this event.
				</p>
			</div>
		`;
		frm.set_df_property('google_calendar_link', 'options', html);
		return;
	}

	// Fallback: Show generic "Add to Calendar" link if event hasn't been created yet
	// (for backward compatibility or if calendar sync is disabled)
	const employee_name = frm.doc.employee_name || frm.doc.employee;
	const leave_type = frm.doc.leave_type || 'Leave';

	// Format dates for Google Calendar (YYYYMMDD format for all-day events)
	const from_date = frm.doc.from_date.replace(/-/g, '');
	// Google Calendar expects end date to be the day AFTER for all-day events
	const to_date_obj = frappe.datetime.str_to_obj(frm.doc.to_date);
	to_date_obj.setDate(to_date_obj.getDate() + 1);
	const to_date = frappe.datetime.obj_to_str(to_date_obj).replace(/-/g, '');

	// Build event title and description
	const title = encodeURIComponent(`${employee_name} - ${leave_type}`);
	const description = encodeURIComponent(`Leave Application: ${frm.doc.name}\nEmployee: ${employee_name}\nType: ${leave_type}\nFrom: ${frm.doc.from_date}\nTo: ${frm.doc.to_date}`);

	// Google Calendar URL for adding an event
	const google_url = `https://calendar.google.com/calendar/render?action=TEMPLATE&text=${title}&dates=${from_date}/${to_date}&details=${description}`;

	// Render HTML with styled link
	const html = `
		<div class="calendar-link-container" style="padding: 8px 0;">
			<p style="margin-bottom: 8px; color: var(--text-muted); font-size: var(--text-sm);">
				Add this leave to your personal Google Calendar:
			</p>
			<a href="${google_url}" target="_blank" class="btn btn-default btn-sm" style="display: inline-flex; align-items: center; gap: 6px;">
				<svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
					<path d="M19.5 3h-15A1.5 1.5 0 003 4.5v15A1.5 1.5 0 004.5 21h15a1.5 1.5 0 001.5-1.5v-15A1.5 1.5 0 0019.5 3zM12 18a6 6 0 110-12 6 6 0 010 12zm0-10.5a4.5 4.5 0 100 9 4.5 4.5 0 000-9z"/>
					<path d="M12 8v4l3 1.5"/>
				</svg>
				Add to Google Calendar
			</a>
			<p style="margin-top: 8px; color: var(--text-muted); font-size: var(--text-xs);">
				Note: If calendar sync is enabled, this event will be automatically added to the shared Leaves Calendar.
			</p>
		</div>
	`;

	frm.set_df_property('google_calendar_link', 'options', html);
}

frappe.ui.form.on('Employee Presence Settings', {
	refresh: function(frm) {
		// Refresh flexitime balance from Employee doctype
		if (frm.doc.employee) {
			frappe.db.get_value('Employee', frm.doc.employee, 'custom_flexitime_balance', (r) => {
				if (r && r.custom_flexitime_balance !== undefined) {
					frm.set_value('flexitime_balance', r.custom_flexitime_balance || 0);
				}
			});
		}

		// Make employee field read-only if document is saved (due to unique constraint)
		if (!frm.is_new()) {
			frm.set_df_property('employee', 'read_only', 1);
		}
	}
});
