script:
frappe.ui.form.on('Leave Application', {
	refresh: function(frm) {
		render_calendar_link(frm);
	},
	status: function(frm) {
		render_calendar_link(frm);
	}
});

function render_calendar_link(frm) {
	if (frm.doc.status !== 'Approved' || !frm.doc.from_date || !frm.doc.to_date) {
		frm.set_df_property('google_calendar_link', 'options', '');
		return;
	}

	// Get employee name for event title
	const employee_name = frm.doc.employee_name || frm.doc.employee;
	const leave_type = frm.doc.leave_type || 'Leave';

	// Format dates for Google Calendar (YYYYMMDD format for all-day events)
	const from_date = frm.doc.from_date.replace(/-/g, '');
	// Google Calendar expects end date to be the day AFTER for all-day events
	const to_date_obj = frappe.datetime.str_to_obj(frm.doc.to_date);
	to_date_obj.setDate(to_date_obj.getDate() + 1);
	const to_date = frappe.datetime.obj_to_str(to_date_obj).replace(/-/g, '');

	// Build event title and description
	const title = encodeURIComponent(`${employee_name} - ${leave_type}`);
	const description = encodeURIComponent(`Leave Application: ${frm.doc.name}\nEmployee: ${employee_name}\nType: ${leave_type}\nFrom: ${frm.doc.from_date}\nTo: ${frm.doc.to_date}`);

	// Google Calendar URL for adding an event
	const google_url = `https://calendar.google.com/calendar/render?action=TEMPLATE&text=${title}&dates=${from_date}/${to_date}&details=${description}`;

	// Render HTML with styled link
	const html = `
		<div class="calendar-link-container" style="padding: 8px 0;">
			<p style="margin-bottom: 8px; color: var(--text-muted); font-size: var(--text-sm);">
				Add this leave to your personal Google Calendar:
			</p>
			<a href="${google_url}" target="_blank" class="btn btn-default btn-sm" style="display: inline-flex; align-items: center; gap: 6px;">
				<svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
					<path d="M19.5 3h-15A1.5 1.5 0 003 4.5v15A1.5 1.5 0 004.5 21h15a1.5 1.5 0 001.5-1.5v-15A1.5 1.5 0 0019.5 3zM12 18a6 6 0 110-12 6 6 0 010 12zm0-10.5a4.5 4.5 0 100 9 4.5 4.5 0 000-9z"/>
					<path d="M12 8v4l3 1.5"/>
				</svg>
				Add to Google Calendar
			</a>
			<p style="margin-top: 8px; color: var(--text-muted); font-size: var(--text-xs);">
				Note: If your company uses a shared calendar subscription, this event may already appear there.
			</p>
		</div>
	`;

	frm.set_df_property('google_calendar_link', 'options', html);
}
